              <option value="Analyse" >Analyse 1</option>
              <option value="Physique"  >Physique 1</option>
              <option value="Algebre1"  >Algebre 1</option>
              <option value="Mecanique"  >MÃ©canique</option>
              <option value="Electronique" >Electronique</option>
              <option value="TEC">TEC</option>
              <option value="Anglais">Anglais</option>
              <option value="Espagnol"  >Espagnol</option>
              <option value="ProgrammationWeb"  >ProgrammationWeb</option>

            </select>

              </span> 
          </h4>
          <h4>Responsable du module : 
          <span>
          <select class="ModuleConsultation" name="responsable" onChange={(e)=>{
              setResponsable(e.target.value)
            }}
            >
              <option disabled selected value="">
                {""}
                ---- SÃ©lectionner ----{" "}
              </option>
              <option value="Prof1" onClick={submitReview} >Prof 1</option>
              <option value="Prof2"  >Prof 2</option>
              <option value="Prof3"  >Prof 1</option>
              <option value="Prof4"  >Prof 4</option>
              <option value="Prof5" >Prof 5</option>
            </select>
        
          </span> 

           </h4> 

           <div class="Description">
           <label for="Desc">Description</label> 
           <input id="Desc" name="description" type="text"  onChange={(e)=>{ setDescription(e.target.value)}} />
           </div>
          <button id="Envoyer2" onClick={submitReview} >
              Envoyer
          </button>
        </form>
        <div class="NoteBien">
            <h4 class="NB">
                N.B : 
            </h4>
