import React , {useState} from 'react';
import NavbarProf from '../Navbar/Navbar';
import './Consultation-copie.css';
import {AiFillCaretDown} from "react-icons/ai";
import { Axios } from 'axios';

function Consultation_de_copie(){
  const [module,setModule]= useState("");
  const [responsable, setResponsable]=useState("");
  const [description, setDescription]=useState("");

  const submitReview=()=>{
      Axios.post("http://localhost:3001/Consultation/", {
        module:module,
        responsable:responsable,
        description:description,
      }).then(()=>{
          console.log("successful insert");
      });
  };
    return (
        <>
        <NavbarProf></NavbarProf>
        <h3 class="Comment">Les étudiants suivants demandent la consultation de leurs copies :</h3>

        <table id="TabSurveillance">
            <select id="document" name="document" onChange={(e)=>{
              setDocument(e.target.value)
            }}>
              <option disabled selected value="">
                {""}
                -- Sélectionner --{" "}
              </option>
              <option value="scolarite" >Attestation de scolarité</option>
              <option value="reussite"  >Attestation de réussite</option>
              <option value="inscription"  >Attestation d’inscription</option>
              <option value="notes"  >Relevé de notes</option>
              <option value="demandeStage" >Demande de stage</option>
              <option value="convocationStage"  >Convocation de stage</option>
            </select>
        <tr>
          <td>Programmation Web</td>
          <td>0001000</td>
          <td>Etudiant 2</td>
          <td></td>
          <td > 
          <button class="Reponse">
                Refuser
            </button>
            <button class="Reponse">
                Accepter
            </button>
          </td>
        </tr>
       
      </table>


        </>
       
      );

}
export default Consultation_de_copie ;
