import React from 'react';
import NavbarProf from '../Navbar/Navbar';
import './Consultation-copie.css';
import {AiFillCaretDown} from "react-icons/ai";
import emailjs from "emailjs-com";

function Consultation_de_copie(){
  function sendEmail(e){
    e.preventDefault();

    emailjs.sendForm('gmail', 'template_e9cy18a', form.current, 'user_W2SEM0j2uJIDKJQPvvSPT')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  }
    return (
        <>
        <NavbarProf></NavbarProf>
        <h3 class="Comment">Les étudiants suivants demandent la consultation de leurs copies :</h3>

        <table id="TabSurveillance">
        <tr>
          <th>Module (ou element du module)</th>
          <th>Numero apogée</th>
          <th> Etudiant</th>
          <th>Message</th>
          <th class="thReponse">Réponse </th>
        </tr>
        <tr>
          <td>POO</td>
          <td>0000000</td>
          <td>Etudiant 1</td>
          <td></td>
          <td>
          <button class="Reponse">
                Refuser
            </button>
            <button class="Reponse">
                Accepter
            </button>
          </td>
        </tr>
        <tr>
          <td>Programmation Web</td>
          <td>0001000</td>
          <td>Etudiant 2</td>
          <td></td>
          <td > 
          <button class="Reponse">
                Refuser
            </button>
            <button class="Reponse" value="Send" type='submit' onClick={sendEmail}>
                Accepter
            </button>
          </td>
        </tr>
       
      </table>


        </>
       
      );

}
export default Consultation_de_copie ;
