          setResult(response.data[k])
        }
      }
        
      console.log("The data above : ");
    
    })
        
  },[])
    
    console.log("Is it ?? : "+ result.Prenom);
    console.log("Is it ?? : "+ result.Nom);
    console.log("Is it ?? : "+ result.APOGE);
    console.log("Is it ?? : "+ result.LieuExamen);
 


// The main return

  return (
    <>
      <NavbarStudent></NavbarStudent>
      <div class="TabExamens">
        <table id="TabExamens">
          <tr>
            <th>Module</th>
            <th>Salle</th>
            <th>Date</th>
            <th>Heure</th>
            <th>Durée</th>
          </tr>
          <tr>
            <td>Programmation Web</td>
            <td>104</td>
            <td>19/05/2020</td>
            <td>14:00</td>
            <td>2h</td>
          </tr>
          <tr>
            <td>Systeme d'exploitation</td>
            <td>203</td>
