import React ,{useState} from "react";
import NavbarStudent from "../Navbar/Navbar";
import "./Consultation-copie.css";
import {AiFillCaretDown} from "react-icons/ai";
import { Axios } from "axios";

function Consultation_copie() {
  const [module,setModule]= useState("");
  const [responsable, setResponsable]=useState("");
  const [Description, setDescription]=useState("");

  const submitReview=()=>{
      Axios.post("http://localhost:3001/etudiant/", {
        module:module,
        responsable:responsable,
        Description:Description,
      }).then(()=>{
          console.log("successful insert");
      });
  };
  return (
    <>
      <NavbarStudent></NavbarStudent>
      <h3 class="Comment">Veuillez remplir les champs ci-dessous</h3>

      <div class="DemandeConsultation">
        <form class="DemandeFormConsultation">
          <h2 class="TitleDemande">Demande de consultation de copie</h2>
          <h4>Module (ou element du module) : 
              <span>
            <select id="ModuleConsultation" name="document" onChange={(e)=>{
              setModule(e.target.value)
            }}>
              <option disabled selected value="">
                {""}
                -- Sélectionner --{" "}
              </option>
              <option value="scolarite" >Attestation de scolarité</option>
              <option value="reussite"  >Attestation de réussite</option>
              <option value="inscription"  >Attestation d’inscription</option>
              <option value="notes"  >Relevé de notes</option>
              <option value="demandeStage" >Demande de stage</option>
              <option value="convocationStage"  >Convocation de stage</option>
            </select>


              </span> 
          </h4>
          <h4>Responsable du module : 
          <span>
                  
                  <div class="dropdown">
                  <button class="dropbtn">Zineb BESRI <AiFillCaretDown/> </button>
                  <div class="dropdown-content">
                      <a href="#">Prof 1</a>
                      <a href="#">Prof 2</a>
                      <a href="#">Prof 3</a>
                  </div>
                  </div>
  
            </span> 

           </h4> 

           <div class="Description">
           <label for="Desc">Description</label> 
           <input id="Desc" name="Desc" type="text" />
           </div>
          <button class="Envoyer">
              Envoyer
          </button>
        </form>
        <div class="NoteBien">
            <h4 class="NB">
                N.B : 
            </h4>
            <p class="NBp">
            Toute demande de consultation de copie dépassant 48 heures à partir d'affichage des résultats
            est considéré refusé
            </p>
        </div>
      </div>
    </>
  );
}
export default Consultation_copie;
