import React , { useRef } from 'react';
import NavbarProf from '../Navbar/Navbar';
import './Consultation-copie.css';
import {AiFillCaretDown} from "react-icons/ai";
import emailjs from "emailjs-com";

function Consultation_de_copie(){

    const form = useRef();

  function sendEmail(e){
    e.preventDefault();

    emailjs.sendForm('service_whve4kl', 'template_e9cy18a', form.current, 'user_W2SEM0j2uJIDKJQPvvSPT')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  }
    return (
        <>
        <NavbarProf></NavbarProf>
        <h3 class="Comment">Les étudiants suivants demandent la consultation de leurs copies :</h3>     
        <table id="TabSurveillance" class="consultationClass">
        <tr>
          <th>Module (ou element du module)</th>
          <th>Numero apogée</th>
          <th> Email de l'étudiant</th>
          <th>Message</th>
          <th class="thReponse">Réponse </th>
        </tr>
        <tr>
          <td>Modélisation et POO</td>
          <td>0000000</td>
          <td>ali.akil@etu.uae.ac.ma</td>
          <td></td>
          <td>
          <button class="Reponse">
                 Accepter
            </button>
            <button class="Reponse">
            Refuser
            </button>
          </td>
        </tr>
        <tr>
          <td>Modélisation et POO</td>
          <td>0001000</td>
          <td>Moad.merroun@etu.uae.ac.ma</td>
          <td></td>
          <td > 
            <form onSubmit={sendEmail}>
            <label>Email</label>
             <input type="email" name="user_email" value="Moad.merroun@etu.uae.ac.ma" />
          <button class="Reponse" type="submit">
               Accepter
            </button>
            <button class="Reponse" value="Send" type="submit">
                Refuser
            </button>
            </form>
          </td>
        </tr>
       
      </table>
      {/* <form ref={form} onSubmit={sendEmail}>
      <label>Name</label>
      <input type="text" name="user_name" />
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <input type="submit" value="Send" />
    </form> */}


        </>
       
      );

}
export default Consultation_de_copie ;
